def solve_puzzle():
    global board

    def solve_rows():
        global board
        for lst in board:
            btn_text = []
            for button in lst:
                if button['text']!='':
                    btn_text.append(button['text'])
            for button in lst:
                if button['bg']!='brown':
                    for i in range(1,16):
                        if button['text']=="":
                            if i not in btn_text:
                                button.configure(text=i)
                                btn_text.append(i)
                                break
                        if button['text']!="":
                            if i not in btn_text:
                                button.configure(text=i)
                                btn_text.append(i)
                                break
    def solve_columns():
        for r1 in range(0, 15):
            col_list = []
            for c1 in range(0, 15):
                button1 = board[c1][r1]
                if button1['text'] == "":
                    for i in range(1,16):
                        if i not in col_list:
                            button1.configure(text=i)
                            col_list.append(i)
                else:
                    col_list.append(button1['text'])
                    if int(col_list.count(button1['text'])) > 1:
                        if button1['bg']=="brown":
                            pass
                        else:
                            for i in range(1, 16):
                                if i not in col_list:
                                    button1.configure(text=i)
                                    col_list.append(i)

        return
    solve_rows()
    solve_columns()







